function associateObjWithEvent(a, b) {
	return (function (e) {
		if (!e) e = window.event;
		return a[b](e)
	})
};
function Xeyes(a, b, c, d, e, f, g, h, i) {
	this.faceObj = document.getElementById(a);
	this.eye1Obj = document.getElementById(b);
	this.e1Lft = c;
	this.e1Top = d;
	this.e1Radius = e;
	this.e1x;
	this.e1y;
	this.r1;
	this.e1xLoc;
	this.e1yLoc;
	this.eyesInit();
	window.onresize = associateObjWithEvent(this, "eyesInit");
	document.onmousemove = associateObjWithEvent(this, "eyesMove")
};
function getPos(a) {
	var b = 0;
	var c = 0;
	while (a != null) {
		b += a.offsetLeft;
		c += a.offsetTop;
		a = a.offsetParent
	};
	return [b, c]
};
Xeyes.prototype.eyesInit = function (e) {
	var a = this.faceObj.offsetWidth;
	var b = this.faceObj.offsetHeight;
	this.e1xLoc = 0.01 * this.e1Lft * a - this.eye1Obj.offsetWidth / 2;
	this.e1yLoc = 0.01 * this.e1Top * b - this.eye1Obj.offsetHeight / 2;
	tmp = getPos(this.faceObj);
	this.e1x = tmp[0] + 0.01 * this.e1Lft * a;
	this.e1y = tmp[1] + 0.01 * this.e1Top * b;
	this.r1 = 0.01 * this.e1Radius * a;
	this.eye1Obj.style.left = this.e1xLoc + "px";
	this.eye1Obj.style.top = this.e1yLoc + this.r1 / 1.5 + "px"
};
Xeyes.prototype.eyesMove = function (e) {
	var a, csrY;
	var x, y;
	var b, dy;
	var R;
	var c = this;
	document.onmousemove = null;
	a = e.clientX + document.documentElement.scrollLeft;
	csrY = e.clientY + document.documentElement.scrollTop;
	b = a - this.e1x;
	dy = csrY - this.e1y;
	R = Math.sqrt(b * b + dy * dy);
	x = (R < this.r1) ? b: b * this.r1 / R;
	y = (R < this.r1) ? dy: dy * this.r1 / R;
	this.eye1Obj.style.left = x + this.e1xLoc + "px";
	this.eye1Obj.style.top = y + this.e1yLoc + "px";
	window.setTimeout(function () {
		document.onmousemove = associateObjWithEvent(c, "eyesMove")
	},
	100)
};